DROP TABLE HAYDAY.PRODUCT;
DROP TABLE HAYDAY.COST;
DROP TABLE HAYDAY.SUPPLY;

-- --------------------------------------------------------------------------
-- --------------------------------------------------------------------------

CREATE TABLE HAYDAY.SUPPLY (
ID SERIAL,
NAME VARCHAR(255),
MASTERSHIP BOOL,
PRIMARY KEY (ID)
);

CREATE TABLE HAYDAY.COST (
ID NUMERIC,
ID_RELATED NUMERIC,
AMOUNT NUMERIC
);

CREATE TABLE HAYDAY.PRODUCT (
ID SERIAL PRIMARY KEY,
NAME VARCHAR(255),
VALUE NUMERIC,
TIME NUMERIC,
ID_SUPPLY SERIAL
);

-- --------------------------------------------------------------------------
-- --------------------------------------------------------------------------

ALTER TABLE HAYDAY.PRODUCT
ADD CONSTRAINT FK_ID_SUPPLY FOREIGN KEY (ID_SUPPLY) REFERENCES HAYDAY.SUPPLY (ID);

